import requests

response = requests.get(
    'http://localhost:37777/api/search',
    params={'q': 'authentication', 'limit': 10}
)

for obs in response.json()['results']:
    print(f"{obs['type']}: {obs['title']}")

const eventSource = new EventSource('http://localhost:37777/stream');

eventSource.addEventListener('observation', (event) => {
  const obs = JSON.parse(event.data);
  console.log('New observation:', obs.title);
});

eventSource.addEventListener('summary', (event) => {
  const summary = JSON.parse(event.data);
  console.log('Session complete:', summary.session_id);
});
